import './App.css';
import { useEffect, useState, useRef} from "react";
import Commands from "./Commands";

function App() {
  const [ input, setInput ] = useState("");
  const [ output, setOutput ] = useState("");
  const inputRef = useRef();


  const helpContent = ReactDOMServer.renderToString(<Commands />); // Obtenez la chaîne de rendu du composant Commands

  useEffect(()=> {
    inputRef.current.focus()
  }, [])

  return (
    <div className="App">
      My Portfolio CLI  [Version 1.0.0] 
      <br/> Andry Sambatra Gloria Flavien RANDRIAMBOLOLONA (c) Not a Corporation.
      <div className='Title'>
        <br/>.. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . ...
        <br/>.. __________ . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . ..
        <br/>..| . . . . .\ . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . ...
        <br/>..|. . ._. . .\ . . . . . . . . . .__ . . . ._____. . . . ____. __. . . . . . ..
        <br/>..| . .|.|. . |. . . . . . . . . ./. | . . ./. . .\. . . | . .|/ .\. . . . . ...
        <br/>..|. . | | . .| . . . . . . . . _/. .|____ /. ._. .\. . .|. . ( . .). . . . . ..
        <br/>..| . .|.|. . |. . . . . . . . | . . . . .|. ./.|__| . . | . .|\__/. . . . . ...
        <br/>..|. . |_| . .|_____. .__ .____|_ . . ____| . |__ _____ .|. . |____ . _____ . ..
        <br/>..| . . . . . /. . .\.|. |/. . \ | . | .|. . . . / . . \ | . .|. . | / . . \ ...
        <br/>..|. . ._____/. ._. .\| . . __. \|. .|. |_. . ._/ . _ . \|. . | . .|/ . _ . \ ..
        <br/>..| . .|. . |. .|.|. .|. . / .\__| . | ___|. .|| . | | . | . .|. . | . | | . |..
        <br/>..|. . | . .| . |_| . | . | . . .|. .\/ . | . ||. .|_|. .|. . | . .|. .|_|. .|..
        <br/>..| . .|. . .\ . . . /|. .|. . . \ . . . /|. .|.\. . . ./| . .|. . |\. . . ./...
        <br/>..|____| . . .\_____/ |___| . . . \_____/ |___| .\_____/.|____|____|.\_____/. ..
        <br/>.. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . ...

      </div>

      <p>
        Type <div className="green"> interface </div> if you want to switch to dynamic mode
      </p>
      <p>
        Type <div className="green"> help </div> to know all commands
      </p>
      <div className="terminal">
        {output}
      </div>
      <div className="input-line">
        <span>
          gloriaflavien@portfolio:~$
        </span>
        <input 
          ref={inputRef}
          type="text" 
          value={input}
          onChange={e=>setInput(e.target.value)}
          onKeyDown={e=>{
            if (e.key === "Enter"){
              let newOutput="";
              newOutput = output + "\n" + "gloriaflavien@portfolio:~$ " + input + "\n" ;
              switch (input) {
                case "help" :
                  newOutput += helpContent();
                  break;
                case "cv" :
                  newOutput += "9 Ampanihy Ouest ";
                  break;
                case "clear" :
                  newOutput = "";
                  break;
                case "interface" : 
                  window.location.href = 'https://www.google.com';
                  break;
                default: 
                  newOutput += "unknown command"
              }
              setOutput(newOutput )
              setInput("")
            }
            if (e.key === "Tab"){
              setOutput("tabulation is clicked");
              setInput("")
            }
          }}

        />
      </div>

    </div>
  );
}

export default App;
