import './App.css';
import { useState} from "react";

function App() {
  const [ input, setInput ] = useState("");
  const [ output, setOutput ] = useState("");
  return (
    <div className="App">

      <div className="terminal">
        {output + "\n"}
      </div>
      <input 
        type="text" 
        value={input}
        onChange={e=>setInput(e.target.value)}
        onKeyDown={e=>{
          if (e.key === "Enter"){
            let newOutput="";
            newOutput = output + "\n" + "$ " + input + "\n" ;
            switch (input) {
              case "help" :
                newOutput += "list of commands (mbola tsy feno nente ) a \n b \n c \n ";
                break;
              default: 
                newOutput += "unknown command"
            }
            setOutput(newOutput )
            setInput("")
          }
          if (e.key === "Tab"){

            setOutput("tabulation is clicked");
            setInput("")
          }
        }}

      />
    </div>
  );
}

export default App;
