// import React, { useState, useEffect, useRef } from 'react';

function App() {
  const [input, setInput] = useState('');
  const [output, setOutput] = useState('');
  const inputRef = useRef();

  useEffect(() => {
    inputRef.current.focus();
  }, []);

  const helpData = [
    { command: "about", description: "Show the owner's personal information of the portfolio." },
    { command: "cv", description: "Link to the CV (curriculum vitae)." },
    { command: "experiences", description: "List all the experiences of the creator of this site." },
    { command: "help", description: "Show all commands." },
    { command: "interface", description: "Toggle the interface into dynamic mode." },
    { command: "project", description: "List large projects already done by the site owner." },
    { command: "social-network", description: "Link to all social networks of the portfolio owner." },
    { command: "technical-skills", description: "List all skills of the portfolio owner." }
  ];

  const handleEnter = (e) => {
    if (e.key === 'Enter') {
      let newOutput = '';
      newOutput += `\ngloriaflavien@portfolio:~$ ${input}\n`;

      switch (input) {
        case 'help':
          newOutput += '<table>';
          helpData.forEach(item => {
            newOutput += `<tr><td>${item.command}</td><td>${item.description}</td></tr>`;
          });
          newOutput += '</table>';
          break;
        case 'cv':
          newOutput += '9 Ampanihy Ouest';
          break;
        case 'clear':
          newOutput = '';
          break;
        case 'interface':
          window.location.href = 'https://www.google.com';
          break;
        default:
          newOutput += 'unknown command';
      }

      setOutput(output + newOutput);
      setInput('');
    }
  };

  return (
    <div className="App">
      My Portfolio CLI  [Version 1.0.0] 
      <br /> Andry Sambatra Gloria Flavien RANDRIAMBOLOLONA (c) Not a Corporation.
      <div className='Title'>
        <br />.. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . ...
      </div>
      <p>
        Type <span className="green">interface</span> if you want to switch to dynamic mode
      </p>
      <p>
        Type <span className="green">help</span> to know all commands
      </p>
      <div className="terminal">
        {output}
      </div>
      <div className="input-line">
        <span>
          gloriaflavien@portfolio:~$
        </span>
        <input
          ref={inputRef}
          type="text"
          value={input}
          onChange={e => setInput(e.target.value)}
          onKeyDown={handleEnter}
        />
      </div>
    </div>
  );
}

export default App;
