import './App.css';
import { useEffect, useState, useRef} from "react";

function App() {
  const [ input, setInput ] = useState("");
  const [ output, setOutput ] = useState("");
  const inputRef = useRef();
  useEffect(()=> {
    inputRef.current.focus()
  }, [])
  return (
    <div className="App">
      My Porfolio CLI  [Version 1.0.0] (c) Not a Corporation.
      <div className="terminal">
        {output}
      </div>
      <div className="inputline">
        <span>
          gloriaflavien@portfolio:~$
        </span>
        <input 
          ref={inputRef}
          type="text" 
          value={input}
          onChange={e=>setInput(e.target.value)}
          onKeyDown={e=>{
            if (e.key === "Enter"){
              let newOutput="";
              newOutput = output + "\n" + "$ " + input + "\n" ;
              switch (input) {
                case "help" :
                  newOutput += "list of  commands (mbola tsy feno nente ) \n "+ "a \n b \n c \n ";
                  break;
                case "cv" :
                  newOutput += "9 Ampanihy Ouest ";
                  break;
                case "clear" :
                  newOutput = "";
                  break;
                case "interface" : 
                  window.location.href = 'https://www.google.com';
                  break;
                default: 
                  newOutput += "unknown command"
              }
              setOutput(newOutput )
              setInput("")
            }
            if (e.key === "Tab"){
              setOutput("tabulation is clicked");
              setInput("")
            }
          }}

        />
      </div>

    </div>
  );
}

export default App;
