import './App.css';
import { useEffect, useState, useRef} from "react";

function App() {
  const [ input, setInput ] = useState("");
  const [ output, setOutput ] = useState("");
  const inputRef = useRef();
  useEffect(()=> {
    inputRef.current.focus()
  }, [])
  return (
    <div className="App">
      My Portfolio CLI  [Version 1.0.0] 
      (c) Not a Corporation.

      <br/>.. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
      <br/>. .__________ . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
      <br/>. | . . . . .\ . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
      <br/>..|. . ._. . .\ . . . . . . . . . .__ . . . ._____. . . . ____. __. . . . . . . .
      <br/>. | . .|.|. . |. . . . . . . . . ./. | . . ./. . .\. . . | . .|/ .\. . . . . . .
      <br/>..|. . | | . .| . . . . . . . . _/. .|____ /. ._. .\. . .|. . ( . .). . . . . . .
      <br/>..| . .|.|. . |. . . . . . . . | . . . . .|. ./.|__| . . | . .|\__/. . . . . . . .
      <br/>..|. . |_| . .|_____. .__ .____|_ . . ____| . |__ _____ .|. . |____ . _____ . . .
      <br/>..| . . . . . /. . .\.|. |/. . \ | . | .|. . . . / . . \ | . .|. . | / . . \ . . .
      <br/>..|. . ._____/. ._. .\| . . __. \|. .|. |_. . ._/ . _ . \|. . | . .|/ . _ . \ . . 
      <br/>..| . .|. . |. .|.|. .|. . / .|__| . | ___|. .|| . | | . | . .|. . | . | | . | . . 
      <br/>..|. . | . .| . |_| . | . | . . .|. .\/ . | . ||. .|_|. .|. . | . .|. .|_|. .|. .
      <br/>..| . .|. . .\ . . . /|. .|. . . \ . . . /|. .|.\. . . ./| . .|. . |\. . . ./. . .
      <br/>..|____| . . .\_____/ |___| . . . \_____/ |___| .\_____/.|____|____|;\_____/. . . 
      <br/> . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .

      <p>
        Type <div className="green"> Interface </div> if you     want to switch to dynamic mode
      </p>
      <div className="terminal">
        {output}
      </div>
      <div className="input-line">
        <span>
          gloriaflavien@portfolio:~$
        </span>
        <input 
          ref={inputRef}
          type="text" 
          value={input}
          onChange={e=>setInput(e.target.value)}
          onKeyDown={e=>{
            if (e.key === "Enter"){
              let newOutput="";
              newOutput = output + "\n" + "gloriaflavien@portfolio:~$ " + input + "\n" ;
              switch (input) {
                case "help" :
                  newOutput += "list of  commands (mbola tsy feno nente ) \n "+ "a \n b \n c \n ";
                  break;
                case "cv" :
                  newOutput += "9 Ampanihy Ouest ";
                  break;
                case "clear" :
                  newOutput = "";
                  break;
                case "interface" : 
                  window.location.href = 'https://www.google.com';
                  break;
                default: 
                  newOutput += "unknown command"
              }
              setOutput(newOutput )
              setInput("")
            }
            if (e.key === "Tab"){
              setOutput("tabulation is clicked");
              setInput("")
            }
          }}

        />
      </div>

    </div>
  );
}

export default App;
